import React from 'react'
import { ROUTES_WITHOUT_HOMEPAGE } from '../Register/constants'
import Axios from 'axios'
import { useState , useEffect} from 'react';
import { useHistory } from 'react-router'
import { setCookie } from 'cookies'
import './login.css'

function validateLoginInput(props){
    Axios.post("http://localhost:4000/login" , {
        mail : props.email,
        pass : props.pass
    }).then((response) => {
        console.log(response.data);
        switch(response.data){
            case true:
                setCookie("email" , props.email , 365);
                props.setRedirect(true);
                break;
            default:
                props.clearInputFields();
                props.setInvalidInput(true);
                break;
        }
    })
}

const Login = (props)=>{
    function clearInputFields(){
        document.getElementById("input-mail").value = "";
        document.getElementById("input-pass").value = "";
    }

    const [email , setEmail] = useState('');
    const [pass , setPass] = useState('');
    const [users , setUsers] = useState([]);
    const [invalidInput , setInvalidInput] = useState(false);
    const [redirect , setRedirect] = useState(false);

    useEffect(()=>{
        Axios.get("http://localhost:4000/all")
        .then(response =>{
            setUsers(response.data);
        })
        .catch(error =>{
            console.log(error);
        })
    } , [])

    let routing = useHistory();
    const currentLocation = props.location.pathname;
    if(ROUTES_WITHOUT_HOMEPAGE[currentLocation]) return null;
    {redirect && routing.push('./home')}
    
    return(
        <div className="login-form h-full bg-blue-500 flex items-center justify-evenly">
            <div className = "h-80 w-1/3 p-4 bg-green-500 flex flex-col">
                <label for = "email" className = "login-info-label"><b>Mail:</b></label>
                <input id = "input-mail" type = "text" placeholder = "Enter mail" name = "email" className="login-info-input" onChange = {(e)=>setEmail(e.currentTarget.value)}></input>
                <label for = "password" className = "login-info-label"><b>Password:</b></label>
                <input id = "input-pass" type = "password" placeholder = "Enter password" name = "password" className="login-info-input" onChange = {(e)=>setPass(e.currentTarget.value)}></input>
                {invalidInput && <h1 className = "login-error">Invalid mail or password!</h1>}
                <button
                    type = "submit"
                    className = "login-submit-button flex items-center justify-center h-12 w-1/2 text-white cursor-pointer bg-red-500"
                    onClick = {() => validateLoginInput({email , pass , users , setRedirect , setInvalidInput , clearInputFields})}>
                        Login
                </button>
            </div>
            <div className="bg-red-500 h-64 w-64 flex items-center justify-center">
                <img src="/images/logo_login.jpg"  alt = "login" className = "rounded-full"/>
            </div>
        </div>
    )
}

export default Login;
