import Axios from 'axios';
import { mapDBIndexMenuItem } from './Constants';

export function addToCart(user , idItem){
    const currEntry = {user : user , idItem : idItem , quantity : 1}
    Axios.post("http://localhost:4000/addUserItem" , {
        entry : currEntry ,
    }).then((response) => {
        console.log(response.data);
    })
}

function editEntriesBD(fetchedData , mapIndexItems){
    fetchedData.forEach(currItem => {
        currItem['ingredients'] = currItem['ingredients'].split(",");
    })
    for(const key in fetchedMenuItems){
        Object.defineProperty(fetchedMenuItems, mapDBIndexMenuItem[key] ,Object.getOwnPropertyDescriptor(fetchedMenuItems , key));
        delete fetchedMenuItems[key];
    }
}

export default editEntriesBD;
