import Axios from 'axios'

export default function validateLoginInput(props){
    Axios.post("http://localhost:4000/login" , {
        mail : props.email,
        pass : props.pass
    }).then((response) => {
        switch(response){
            case true:
                props.setRedirect(true);
            case false:
                props.clearInputFields();
                props.setInvalidInput(true);
            case "Error!":
                
        }
    })
}
