import clearInputFields from 'Components/Login'

export default function validateLoginInput(props){
    props.users.forEach((user)=>{
        if(user.email === props.email && user.pass === props.pass){
            props.setRedirect(true)
        }else{
            clearInputFields();
        }
    })
}
