import Axios from 'axios';

export function addToCart(user , idItem){
    const currEntry = {user : user , idItem : idItem , quantity : 1}
    Axios.post("http://localhost:4000/addUserItem" , {
        entry : currEntry ,
    }).then((response) => {
        console.log(response.data);
    })
}

function editMenuEntries(fetchedData , mapIndexMenuItem){
    fetchedData.forEach(currItem => {
        currItem['ingredients'] = currItem['ingredients'].split(",");
    })
    for(const key in fetchedData){
        Object.defineProperty(fetchedData, mapIndexMenuItem[key] ,Object.getOwnPropertyDescriptor(fetchedData , key));
        delete fetchedData[key];
    }
    return fetchedData;
}

export default editMenuEntries;
