import React from 'react'

const Info = () =>{

    const callback = function (entries) {
      entries.forEach((entry) => {
        console.log(entry);
    
        if (entry.isIntersecting) {
          entry.target.classList.add("animate-fadeIn");
        } else {
          entry.target.classList.remove("animate-fadeIn");
        }
      });
    };
    
    const observer = new IntersectionObserver(callback);
    
    const targets = document.querySelectorAll(".js-show-on-scroll");
    targets.forEach(function (target) {
      target.classList.add("opacity-0");
      observer.observe(target);
    });
  

    return(
        <div className = "h-4/5 w-full mt-52 bg-black">
            <div className = "info-center-items">
                <p className = "info-header-styling">Pasterino , Cluj-Napoca's italian delight!</p>
            </div>
            <div className = "info-center-items js-show-on-scroll">
                <p className = "info-p-styling">We are delighted that our doors are now open.</p>
                <p className = "info-p-styling">Working with the freshest ingredients, imported pasta, and top quality cuts of meat, Pasterino. owes a great deal of its successful menu to its purveyors such as Middendorf Meats, Joe Fazio’s Bakery, DiMare’s Specialty Foods, Louisa Foods, Allen Foods, Paramount Liquor, Grey Eagle Distributors, Lohr Distributors, and Coca-Cola.</p>
                <p className = "info-p-styling">In an effort to provide dining patrons with new tastes in Italian cuisine, The Pasta House Co. works on an ongoing basis to improve existing dishes and to develop new menu items that appeal to today’s increasing number of consumers who insist on consistently high-quality, good tasting Italian fare with authentic “old country” flavor.</p>
                <p className = "info-p-styling">xxxxxxx</p>
            </div>
        </div>
    );
}

export default Info;
