import menuItems from "Menu/Constants";

function usefulCartData(userData){
    var convertedData = [];
    userData.forEach((userItem) => {
        for(let key in menuItems){
            console.log(key , menuItems[key]);
        }
        const newConvertedEntry = {
            menuItem : menuItems[userItem.idItem] ,
            quantity : userItem.quantity
        }
        convertedData.push(newConvertedEntry);
    })
    console.log(convertedData);
}

export default usefulCartData;
