import React from 'react'
import { useState } from 'react';
import { useEffect } from 'react';
import Axios from "axios"
import ReviewCard from './ReviewCard';

const Reviews = () => {

    const [reviews , setReviews] = useState([]);
    const [counter , setCounter] = useState(1);
 
    useEffect(()=>{
        Axios.get("http://localhost:4000/all_reviews")
        .then(response =>{
            setReviews(response.data);
        })
        .catch(error =>{
            console.log(error);
        })
    } , [])

    function getReviewsID(startPos){
        switch(startPos){
            case(reviews.length-1):
                return [reviews.length-1 , reviews.length , 1];
            case(reviews.length):
                return [reviews.length , 1 , 2];
            default:
                return [startPos , startPos + 1 , startPos + 2];
        }
    }

    function wrapperGetReviewID(cardNumber){
        var reviewsIndex = getReviewsID(counter);
        console.log(reviewsIndex);
        console.log(reviewsIndex[0]);
        console.log(reviews[reviewsIndex[0]-1]);
        console.log(reviewsIndex[1]);
        console.log(reviews[reviewsIndex[1]-1]);
        console.log(reviewsIndex[2]);
        console.log(reviews[reviewsIndex[2]-1]);
    }
    
    wrapperGetReviewID(1);
    // console.log(wrapperGetReviewID(1));
    // console.log(wrapperGetReviewID(1));
    // console.log(wrapperGetReviewID(2));
    // console.log(wrapperGetReviewID(3));
    // console.log(reviews[wrapperGetReviewID(1)]);
    // console.log(reviews[wrapperGetReviewID(2)]);
    // console.log(reviews[wrapperGetReviewID(3)]);

    return (
        <div className = "grid grid-areas-reviews-layout gap-5 h-96 w-11/12 -mt-5">
            {/* <ReviewCard id = {1} review = {reviews[wrapperGetReviewID(1)]}/>
            <ReviewCard id = {2} review = {reviews[wrapperGetReviewID(2)]}/>
            <ReviewCard id = {3} review = {reviews[wrapperGetReviewID(3)]}/> */}
            <div className = "grid-in-buttons bg-green-800">
                <div className = "flex justify-around items-center h-full">
                    <button className = "bg-white w-14" onClick = {() => {setCounter(counter % reviews.length + 1)}}>+</button>
                    <button className = "bg-white w-14" onClick = {() => {counter!=1 ? setCounter(counter % reviews.length - 1) : setCounter(reviews.length)}}>-</button>
                </div>
            </div>
        </div>
    )
}

export default Reviews;
