import React from 'react'

const Info = () =>{

    const callback = function (entries) {
        entries.forEach((entry) => {
          console.log(entry);
      
          if (entry.isIntersecting) {
            entry.target.classList.add("animate-fadeIn");
          } else {
            entry.target.classList.remove("animate-fadeIn");
          }
        });
      };
      
      const observer_header = new IntersectionObserver(callback);
      
      const targets = document.querySelectorAll(".info-title-fading");
      targets.forEach(function (target) {
        target.classList.add("opacity-0");
        observer_header.observe(target);
      });

    return(
        <div className = "h-4/5 w-full mt-52 bg-black">
            <div className = "info-center-items">
                <p className = "info-header-styling info-title-fading">Pasterino , Cluj-Napoca's italian delight!</p>
            </div>
            <div className = "info-center-items">
                <p className = "text-2xl p-32 font-general-font text-white">We are delighted that our doors are now open.</p>
                <p className = "text-2xl p-32 font-general-font text-white -mt-52">xx</p>
            </div>
        </div>
    );
}

export default Info;
