import React from 'react'
import { ROUTES_WITHOUT_HOMEPAGE } from '../Register/constants'
import { useHistory } from 'react-router'
import { useState } from 'react'
import { eraseCookie , getCookie } from 'cookies'
import NavigationRegister from '../HomeSubComp/NavigationRegister'
import NavigationLinks from '../HomeSubComp/NavigationLinks'
import NavigationLogo from '../HomeSubComp/NavigationLogo'
import HomeCard from '../HomeSubComp/HomeCard'
import Info from '../Info/Info'
import Menu from '../Menu/Menu'
import Footer from '../Footer/Footer'

import './Home.css'

const Home = (props)=>{

    const clearCookies = () =>{
        eraseCookie("email");
        setLoggedUser(null);
    }

    const [loggedUser , setLoggedUser] = useState(null);
    
    if(getCookie("email") != null && loggedUser == null){
        setLoggedUser(getCookie("email"));
    }

    let routing = useHistory();
    const currentLocation = props.location.pathname;
    if(ROUTES_WITHOUT_HOMEPAGE[currentLocation]) return null;

    return( 
        <div className = "grid gap-10 my-5 mx-10 p-0 box-border overflow-hidden">
            <div className = "w-screen h-20 bg-transparent">
                <div className = "flex flex-row justify-evenly">
                    <NavigationLogo />
                    <NavigationLinks />
                </div>
            </div>
            <div className = "bg-home-section bg-cover bg-center w-screen h-screen rounded-full">

            </div>  
            <div className = "bg-yellow-600 w-screen h-60">

            </div>  
            <div className = "bg-yellow-600 w-screen h-60">

            </div>  
            <div className = "bg-yellow-600 w-screen h-60">

            </div>  
            <div className = "bg-yellow-600 w-screen h-60">

            </div>  
        </div>
    );
}

export default Home;
