import React from 'react'
import { useState } from 'react';
import { useEffect } from 'react';
import Axios from "axios"

const Reviews = () => {

    const [reviews , setReviews] = useState([]);
    const [counter , setCounter] = useState(1);
 
    useEffect(()=>{
        Axios.get("http://localhost:4000/all_reviews")
        .then(response =>{
            setReviews(response.data);
        })
        .catch(error =>{
            console.log(error);
        })
    } , [])

    reviews.forEach((review) => {
        console.log(review.id , review.picture , review.name , review.info , review.rating);
    })
    
    return (
        <div className = "grid grid-areas-reviews-layout gap-5 h-96 w-11/12 -mt-5">
            <div className = "grid-in-review1 bg-yellow-600">

            </div>
            <div className = "grid-in-review2 bg-yellow-600">
                
            </div>
            <div className = "grid-in-review3 bg-yellow-600">
                
            </div>
            <div className = "grid-in-buttons bg-green-800">
                <div className = "flex justify-center items-center h-full">
                    <button className = "bg-white w-14">+</button>
                    <button className = "bg-white w-14">-</button>
                </div>
            </div>
        </div>
    )
}

export default Reviews;
