import React from 'react'

const Category = (props) =>{

    const categoryName = props.name;
    const showCategory = props.value;
    const setShowCategory = props.setter;
    const stylingClassName = "menu-category-" + categoryName + "-selected";
    console.log(stylingClassName)

    return(
        <button className = "menu-category-button" onClick = {()=>{setShowCategory(!showCategory)}}>
            {!showCategory && <h1 className = "menu-category-text">{categoryName.charAt(0).toUpperCase()+categoryName.slice(1)}</h1>}
            {showCategory && <h1 className = "menu-category-text">{categoryName.charAt(0).toUpperCase()+categoryName.slice(1)}</h1>}
        </button>
    );
}

export default Category;
