import React from 'react'

const MenuItem = (props) =>{
    return(
        <div className = "menu-item menu_item_css">
            <div className = {`menu-item-category ${props.color}`}/>
            <div className = "menu-item-image-container">
                <div className = {`menu-item-image bg-${props.image} menu_item_img_css`}></div>
            </div>
            <div className = "menu_item_info_container_css">
                <div className = "menu-item-header-container">
                    <h1 className = "menu-item-header">{props.title}</h1>
                </div>
                <div className = "menu-item-ingredients-container">
                    {props.ingredients.map((ingredient)=>{
                        return(
                            <div className = "menu-item-ingredient">
                                <img className = "menu-item-ingredient-icon" src = "https://cdn-icons-png.flaticon.com/512/988/988919.png"/>
                                <h1>{ingredient}</h1>
                            </div>
                        );
                    })}
                </div>
            </div>
        </div>
    );
}

export default MenuItem;
