import Axios from 'axios'

export default function validateLoginInput(props){
    Axios.post("http://localhost:4000/login" , {
        mail : props.email,
        pass : props.pass
    }).then((response) => {
        if(response){
            props.setRedirect(true);
        }else{
            props.clearInputFields();
            props.setInvalidInput();
        }
    })
}
