import Axios from 'axios';

export default function addToCart(user , idItem){
    Axios.post("http://localhost:4000/addUserItem" , {
        idItem : idItem,
        user : user,
    }).then((response) => {
        console.log(response.data);
        if(response.data.exists){
            const updateEntry = {idItem : idItem , user : user , quantity : response.data.quantity + 1}
            Axios.post("http://localhost:4000/updateUserItem" , {
                entry : updateEntry,
            }).then(() => {
                console.log("Menu item updated succesfully!");
            })
        }else{
            const addEntry = {idItem : idItem , user : user , quantity : 1}
            Axios.post("http://localhost:4000/addUserItem" , {
                entry : addEntry,
            }).then(() => {
                console.log("Menu item added succesfully!");
            })
        }
    })
}
