import React from 'react'
import { ROUTES_WITHOUT_HOMEPAGE } from '../Register/constants'
import { useHistory } from 'react-router'
import { useState } from 'react'
import { eraseCookie , getCookie } from 'cookies'
import NavBar from 'HomeSubComp/NavBar'

const Home = (props)=>{

    const clearCookies = () =>{
        eraseCookie("email");
        setLoggedUser(null);
    }

    const [loggedUser , setLoggedUser] = useState(null);
    
    if(getCookie("email") != null && loggedUser == null){
        setLoggedUser(getCookie("email"));
    }

    let routing = useHistory();
    const currentLocation = props.location.pathname;
    if(ROUTES_WITHOUT_HOMEPAGE[currentLocation]) return null;

    return( 
        <div className = "grid gap-5 ml-10 my-5 p-0 box-border max-w-full overflow-hidden">
            <NavBar />
            <div className = "bg-home-section bg-cover grayscale bg-center w-11/12 h-screen rounded-full -mt-5 relative">
                <div className = "absolute left-1/2 border-2 border-black rounded h-full"></div>
                <div className = "flex flex-row justify-evenly w-full absolute top-1/2">
                    <button className = "text-5xl font-artistic font-extrabold mr-32">Login</button>
                    <button className = "text-5xl font-artistic font-extrabold ml-32">Register</button>
                </div>
            </div>  
        </div>
    );
}

export default Home;
