import React from 'react'
import { useState } from 'react';
import Categories from './MenuSubComp/Categories';

const Menu = () =>{

    const [lasagna , setLasagna] = useState(false);
    const [spaghetti , setSpaghetti] = useState(false);
    const [farfalle , setFarfalle] = useState(false);
    const [macaroni , setMacaroni] = useState(false);

    console.log(lasagna);

    return(
        <div id = "menu" className = "menu bg-yellow-600">
            <div className = "menu-header-section">
                <h1 className = "menu-header">Menu</h1>
            </div>
            <div className = "menu-categories-section">
                {menuCategories.map((category)=>{

                    const currentCategory = "category" + category;
                    const setterCurrentCategory = "setCategory" + category + "(" + "!" + "category" + category + ")";

                    return(
                        <button className = "menu-category-button" onClick = {()=>{eval(setterCurrentCategory)}}>
                            {!eval(currentCategory) && <h1 className = "menu-category-text-unselected">{category}</h1>}
                            {eval(currentCategory) && <h1 className = "menu-category-text-selected">{category}</h1>}
                        </button>
                    );
                })}
            </div>
            <div className = "menu-categories-section">
                {currentCategories.map((category)=>{
                    const setterCategory = 'set' + category.charAt(0).toUpperCase()+category.slice(1);
                    <Category value = {eval(category)} setter = {eval(setterCategory)}/>
                })}
            </div>
            <Categories Lasagna = {lasagna} Spaghetti = {spaghetti} Farfalle = {farfalle} Macaroni = {macaroni}
            setLasagna = {setLasagna} setSpaghetti = {setSpaghetti} setFarfalle = {setFarfalle} setMacaroni = {setMacaroni}/>
            <div className = "menu-items-container">
                <div className = "menu-item">
                    <h1 className = "menu-item-header">LetsGo!</h1>
                    <p>Bla bla bla bla</p>
                </div>
            </div>
        </div>
    )
}

export default Menu;
