import React from 'react'
import { useState } from 'react';
import menuCategories from 'Utils/Menu/Constants';
import Categories from './MenuSubComp/Categories';

const Menu = () =>{

    const [categoryLasagna , setCategoryLasagna] = useState(false);
    const [categorySpaghetti , setCategorySpaghetti] = useState(false);
    const [categoryFarfalle , setCategoryFarfalle] = useState(false);
    const [categoryMacaroni , setCategoryMacaroni] = useState(false);

    const dictCategories= {
        Lasagna : setCategoryLasagna,
        Spaghetti : setCategorySpaghetti,
        Farfalle : setCategoryFarfalle,
        Macaroni : setCategoryMacaroni
    }

    console.log(categoryLasagna , categorySpaghetti , categoryFarfalle , categoryMacaroni);

    return(
        <div id = "menu" className = "menu bg-yellow-600">
            <div className = "menu-header-section">
                <h1 className = "menu-header">Menu</h1>
            </div>
            {/* <div className = "menu-categories-section">
                {menuCategories.map((category)=>{

                    const currentCategory = "category" + category;
                    const setterCurrentCategory = "setCategory" + category + "(" + "!" + "category" + category + ")";

                    return(
                        <button className = "menu-category-button" onClick = {()=>{eval(setterCurrentCategory)}}>
                            {!eval(currentCategory) && <h1 className = "menu-category-text-unselected">{category}</h1>}
                            {eval(currentCategory) && <h1 className = "menu-category-text-selected">{category}</h1>}
                        </button>
                    );
                })}
            </div> */}
            <Categories dict = {dictCategories} />
            <div className = "menu-items-container">
                <div className = "menu-item">
                    <h1 className = "menu-item-header">LetsGo!</h1>
                    <p>Bla bla bla bla</p>
                </div>
            </div>
        </div>
    )
}

export default Menu;
